       }
    } else if (_local.masterStatus == 'off') {
        _local.lastStatus = _local.masterStatus;
log('got status , brightness: ' + _local.masterStatus +',' +  _brightness); 
      if( _brightness > 0 ) {
         return '[#]dim';
      } 
       
    }
//}

}

var colorButtons = ['color-spring', 'color-orange','color-rose','color-charreuse','color-asure'];


if(_local.curColorIdx == undefined ) {
  _local.curColorIdx = 0;
} else {
