[quiet]
str = _feedback;
if (str &amp;&amp; str.startsWith('<?xml')) {
startStr ='<MasterVolume><value>';
start = str.indexOf(startStr);
end = str.indexOf('</value></MasterVolume>', start);

vol = str.substring(start + startStr.length, end);
convertedVol  = convertToRange(vol,[-30,-10],[42,62]);

_setSelectedIndex('volume-slider', convertedVol);
_setText('VolumeLevel', vol);

//log('volume-check avr' + vol  + ', ' + convertedVol +',' + _getSelectedIndex('volume-slider') );
