_local.processFeedback = function(feedback) {
	if (feedback.indexOf('DIRECTV') > 0) {
		var location = '';
		var pieces = feedback.split('\r\n');
		for (i=0; i<pieces.length; i++) {
			if (pieces[i].toLowerCase().indexOf('location:') >= 0) {
				location = pieces[i].replace(/location:/ig, '').replace(/ /g, '');
				var address = location.split('//')[1].split('/')[0];
				_setProperty('DirecTVDescription', 'HTTPHeaders', 'TC-LocationAddress=' + address.split(':')[0]);
				_setProperty('DirecTVDescription', 'address', address);
				return '[#]DirecTVDescription';
			}
		}
	}
}

