var responseHeaders = _property.HTTPResponseHeaders.split('^');
var localAddress = '';
for (var header in responseHeaders) {
	if (responseHeaders[header].split('=')[0] == 'TC-LocationAddress') {
		localAddress = responseHeaders[header].split('=')[1];
		break;
	}
}
