			_setText('CurrentReceiver', receiverName);
		}
		_setText('CurrentIP', localAddress);
		_setActivityTitle(receiverName);
	}
	if (items.indexOf(receiverName) < 0) {
		items.push(receiverName);
		_local.directvReceiversFound++;
		_vibrate(1);
		_setItems('HostSpinner', items);
		_setText('FoundReceivers', 'Found ' + _local.directvReceiversFound + ' receivers');
	}
	if (_getSelectedItem('HostSpinner') == receiverName) {
